      if (timeoutId) {
        clearTimeout(timeoutId);
        timeoutId = null;
      }
      
      const text = event.results[0][0].transcript || '';
      const digits = text.replace(/[^0-9]/g, '');
      setVoiceStatus('processing');
      
      if (digits.length >= 4) {
        const pin = digits.slice(0,4);
        setPassword(pin);
        setVoiceStatus('recognized');
        
        if (voiceOnly) {
          try {
            const user = await voiceLogin(pin);
            if (user && user.id) {
              onLogin(user);
            } else {
              setError('‚ùå –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –Ω–µ –Ω–∞–π–¥–µ–Ω –ø–æ –≥–æ–ª–æ—Å–æ–≤–æ–º—É –ø–∞—Ä–æ–ª—é');
              setVoiceStatus('idle');
            }
          } catch (err) {
            setError('‚ùå ' + (err.detail || '–û—à–∏–±–∫–∞ –≥–æ–ª–æ—Å–æ–≤–æ–π –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏'));
            setVoiceStatus('idle');
          }
