import React, { useState, useEffect } from "react";
import { fetchUsers, login } from "../api"; // —É–±–µ–¥–∏—Å—å, —á—Ç–æ –ø—É—Ç—å –ø—Ä–∞–≤–∏–ª—å–Ω—ã–π

export default function LoginSelect({ onLogin }) {
  const [users, setUsers] = useState([]);
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  // –ó–∞–≥—Ä—É–∑–∫–∞ —Å–ø–∏—Å–∫–∞ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π
  useEffect(() => {
    fetchUsers()
      .then(data => {
        if (Array.isArray(data)) {
          setUsers(data);
        } else {
          throw new Error("–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –¥–∞–Ω–Ω—ã—Ö");
        }
      })
      .catch(err => {
        console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π:", err);
        setError("‚ùå –ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π");
      });
  }, []);

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ –≤—Ö–æ–¥–∞
  const handleLogin = async () => {
    setError("");

    if (!userId || !password) {
      setError("‚ö†Ô∏è –£–∫–∞–∂–∏—Ç–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –∏ –ø–∞—Ä–æ–ª—å");
      return;
    }

    setLoading(true);
    try {
      await login(Number(userId), password);
      const user = users.find(u => u.user_id === Number(userId));
      if (!user) {
        setError("‚ùå –ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –Ω–µ –Ω–∞–π–¥–µ–Ω");
        return;
      }
      onLogin(user);
    } catch (e) {
      console.error("–û—à–∏–±–∫–∞ –≤—Ö–æ–¥–∞:", e);
      setError(e?.detail || "‚ùå –ù–µ–≤–µ—Ä–Ω—ã–π –ø–∞—Ä–æ–ª—å –∏–ª–∏ –æ—à–∏–±–∫–∞ —Å–µ—Ä–≤–µ—Ä–∞");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h2>üîê –í—ã–±–æ—Ä –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è</h2>

      <select value={userId} onChange={e => setUserId(e.target.value)}>
        <option value="">‚Äî –í—ã–±–µ—Ä–∏—Ç–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è ‚Äî</option>
        {users.map(user => (
          <option key={user.user_id} value={user.user_id}>
            {user.name || user.username}
          </option>
        ))}
      </select>

      <input
        type="password"
        placeholder="–ü–∞—Ä–æ–ª—å"
        value={password}
        onChange={e => setPassword(e.target.value)}
        style={{ marginTop: 10 }}
      />

      {error && <div style={{ color: "red", marginTop: 10 }}>{error}</div>}

      <button onClick={handleLogin} disabled={loading} style={{ marginTop: 10 }}>
        {loading ? "–í—Ö–æ–¥..." : "–í–æ–π—Ç–∏"}
      </button>
    </div>
  );
}
