import React, { useState, useEffect } from "react";
import { fetchUsers, login, voiceLogin } from "../api";
import "./LoginSelect.css";

export default function LoginSelect({ onLogin }) {
  const [users, setUsers] = useState([]);
  const [userId, setUserId] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [filter, setFilter] = useState("all");
  const [listening, setListening] = useState(false);
  const [recognitionSupported, setRecognitionSupported] = useState(false);
  const [voiceOnly, setVoiceOnly] = useState(false);
  const getFilteredUsers = () => {
    if (filter === "all") return users;
    if (filter === "vushka") {
      return users.filter(u => u.name.startsWith("–†–ü–ê") || u.name.startsWith("–°—Ç.–î–ü–ê"));
    }
    if (filter === "zal") {
      return users.filter(u => u.name.startsWith("–†–ü–†") || u.name.startsWith("–°—Ç.–†–¶"));
    }
    return users;
  };

  const filteredUsers = getFilteredUsers();

  useEffect(() => {
    fetchUsers()
      .then(setUsers)
      .catch(err => {
        console.error("–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π:", err);
        setError("–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–≥—Ä—É–∑–∏—Ç—å –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π");
      });
  }, []);

  const handleLogin = async () => {
    setError("");
    try {
      await login(Number(userId), password);
      const user = users.find(u => u.id === Number(userId));
      onLogin(user);
    } catch (e) {
      setError(e.detail || "–û—à–∏–±–∫–∞ –≤–≤–æ–¥–∞ –ø–∞—Ä–æ–ª—è");
    }
  };

  // Voice recognition: listens for spoken 4-digit password (digits only)
  useEffect(() => {
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition || null;
    if (!SpeechRecognition) {
      setRecognitionSupported(false);
      return;
    }
    setRecognitionSupported(true);
    const recog = new SpeechRecognition();
    recog.lang = 'ru-RU';
    recog.interimResults = false;
    recog.maxAlternatives = 1;

    recog.onresult = async (event) => {
      const text = event.results[0][0].transcript || '';
      const digits = text.replace(/[^0-9]/g, '');
      if (digits.length >= 4) {
        const pin = digits.slice(0,4);
        setPassword(pin);
        if (voiceOnly) {
          try {
            const user = await voiceLogin(pin);
            if (user && user.id) {
              onLogin(user);
            } else {
              setError('–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –Ω–µ –Ω–∞–π–¥–µ–Ω –ø–æ –≥–æ–ª–æ—Å–æ–≤–æ–º—É –ø–∞—Ä–æ–ª—é');
            }
          } catch (err) {
            setError(err.detail || '–û—à–∏–±–∫–∞ –≥–æ–ª–æ—Å–æ–≤–æ–π –∞–≤—Ç–æ—Ä–∏–∑–∞—Ü–∏–∏');
          }
        } else {
          // auto submit if user is selected
          if (userId) {
            setTimeout(() => handleLogin(), 300);
          }
        }
      } else {
        setError('–†–∞—Å–ø–æ–∑–Ω–∞–Ω–æ –º–µ–Ω—å—à–µ 4 —Ü–∏—Ñ—Ä, –ø–æ–≤—Ç–æ—Ä–∏—Ç–µ');
      }
      setListening(false);
      setVoiceOnly(false);
    };

    recog.onerror = (e) => {
      let errorMsg = '–û—à–∏–±–∫–∞ —Ä–∞—Å–ø–æ–∑–Ω–∞–≤–∞–Ω–∏—è: ' + (e.error || 'unknown');
      if (e.error === 'not-allowed') {
        errorMsg = '‚ùå –î–æ—Å—Ç—É–ø –∫ –º–∏–∫—Ä–æ—Ñ–æ–Ω—É –∑–∞–ø—Ä–µ—â—ë–Ω. –†–∞–∑—Ä–µ—à–∏—Ç–µ –¥–æ—Å—Ç—É–ø –≤ –Ω–∞—Å—Ç—Ä–æ–π–∫–∞—Ö –±—Ä–∞—É–∑–µ—Ä–∞ (–∫–Ω–æ–ø–∫–∞ —Å –∏–∫–æ–Ω–∫–æ–π –º–∏–∫—Ä–æ—Ñ–æ–Ω–∞ –≤ –∞–¥—Ä–µ—Å–Ω–æ–π —Å—Ç—Ä–æ–∫–µ)';
      } else if (e.error === 'no-speech') {
        errorMsg = '‚ùå –ú–∏–∫—Ä–æ—Ñ–æ–Ω –Ω–µ –æ–±–Ω–∞—Ä—É–∂–∏–ª —Ä–µ—á—å. –ü–æ–≤—Ç–æ—Ä–∏—Ç–µ –ø–æ–ø—ã—Ç–∫—É.';
      } else if (e.error === 'network') {
        errorMsg = '‚ùå –û—à–∏–±–∫–∞ —Å–µ—Ç–∏ –ø—Ä–∏ —Ä–∞—Å–ø–æ–∑–Ω–∞–≤–∞–Ω–∏–∏ —Ä–µ—á–∏.';
      }
      setError(errorMsg);
      setListening(false);
    };

    // attach to ref on demand via startListening
    let active = false;
    const startListening = () => {
      if (active) return;
      try {
        recog.start();
        active = true;
        setListening(true);
      } catch (e) {
        setError('–ù–µ —É–¥–∞–ª–æ—Å—å –∑–∞–ø—É—Å—Ç–∏—Ç—å —Ä–∞—Å–ø–æ–∑–Ω–∞–≤–∞–Ω–∏–µ');
        setListening(false);
      }
    };

    // expose startListening via window for this component scope
    (window).__startVoiceLogin = startListening;

    return () => {
      try { recog.stop(); } catch (e) {}
      (window).__startVoiceLogin = null;
    };
  }, [userId, voiceOnly]);

  const triggerVoice = () => {
    setError('');
    if (!recognitionSupported) {
      setError('–†–∞—Å–ø–æ–∑–Ω–∞–≤–∞–Ω–∏–µ —Ä–µ—á–∏ –Ω–µ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ—Ç—Å—è –≤ —ç—Ç–æ–º –±—Ä–∞—É–∑–µ—Ä–µ');
      return;
    }
    // indicate this recognition is mic-initiated (voice-only auth)
    setVoiceOnly(true);
    if (window.__startVoiceLogin) window.__startVoiceLogin();
  };

  return (
    <div className="login-container">
      <h2>üîë –í—Ö–æ–¥</h2>

      <div className="filter-buttons">
        <button 
          className={`filter-btn ${filter === "all" ? "active" : ""}`}
          onClick={() => { setFilter("all"); setUserId(""); }}
        >
          –í—Å–µ
        </button>
        <button 
          className={`filter-btn ${filter === "vushka" ? "active" : ""}`}
          onClick={() => { setFilter("vushka"); setUserId(""); }}
        >
          –í—ã—à–∫–∞
        </button>
        <button 
          className={`filter-btn ${filter === "zal" ? "active" : ""}`}
          onClick={() => { setFilter("zal"); setUserId(""); }}
        >
          –ó–∞–ª
        </button>
      </div>

      <select value={userId} onChange={e => setUserId(e.target.value)}>
        <option value="">–í—ã–±–µ—Ä–∏—Ç–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è</option>
        {filteredUsers.map(user => (
          <option key={user.id} value={user.id}>
            {user.name}
          </option>
        ))}
      </select>

      <div className="input-row">
        <input
          type="password"
          placeholder="–ü–∞—Ä–æ–ª—å (4 —Ü–∏—Ñ—Ä—ã)"
          value={password}
          onChange={e => setPassword(e.target.value)}
          maxLength={4}
          pattern="[0-9]*"
          inputMode="numeric"
        />
        <button className={`mic-btn ${listening ? 'listening' : ''}`} onClick={triggerVoice} title="–í–≤–µ—Å—Ç–∏ –ø–∞—Ä–æ–ª—å –≥–æ–ª–æ—Å–æ–º">üé§</button>
      </div>

      {error && <div style={{ color: "red", marginBottom: "10px" }}>{error}</div>}

      <button onClick={handleLogin}>–í–æ–π—Ç–∏</button>
    </div>
  );
}
