import React, {useEffect, useState} from "react";
import { fetchChecklist, submitReport } from "../api";

export default function ChecklistPlayer({user, checklistId, onDone}) {
  const [checklist, setChecklist] = useState(null);
  const [index, setIndex] = useState(0);
  const [checkedIds, setCheckedIds] = useState([]);
  const [comment, setComment] = useState("");
  const [meta, setMeta] = useState({
    date_of_incident:"", flight_number:"", place:"", time_of_incident:""
  });

  useEffect(() => {
    fetchChecklist(checklistId).then(setChecklist);
    setIndex(0);
    setCheckedIds([]);
    setComment("");
  }, [checklistId]);

  if (!checklist) return <div>Загрузка...</div>;

  const items = checklist.items;
  const current = items[index];

  const markChecked = (id) => {
    if (!checkedIds.includes(id)) setCheckedIds([...checkedIds, id]);
  };

  const skip = () => { setIndex(i => Math.min(items.length - 1, i + 1)); };
  const back = () => { setIndex(i => Math.max(0, i - 1)); };
  const toFirst = () => { setIndex(0); };
  const onTemplate = () => { alert("Шаблон для данного шага: (здесь можно подставить инструкцию)"); };

  const onNextAfterCheck = () => {
    markChecked(current.id);
    if (index < items.length - 1) setIndex(index + 1);
  };

  const handleSubmit = async () => {
    const payload = {
      user_id: user.id,
      checklist_id: checklist.id,
      date_of_incident: meta.date_of_incident,
      flight_number: meta.flight_number,
      place: meta.place,
      time_of_incident: meta.time_of_incident,
      checked_item_ids: checkedIds,
      comment
    };
    try {
      await submitReport(payload);
      alert("Отчет отправлен");
      onDone();
    } catch (e) {
      alert("Ошибка отправки");
    }
  };

  return (
    <div style={{padding:20}}>
      <h3>{checklist.title}</h3>
      <div style={{marginTop:10}}>
        <div><strong>Пункт {index+1} / {items.length}</strong></div>
        <div style={{border:"1px solid #ccc", padding:10, marginTop:10}}>
          {current.text}
        </div>
        <div style={{marginTop:10}}>
          <button onClick={toFirst}>Вернуться к пункту 1</button>
          <button onClick={back}>Назад</button>
          <button onClick={skip}>Пропустить</button>
          <button onClick={onTemplate}>Шаблон</button>
        </div>
        <div style={{marginTop:10}}>
          <button onClick={onNextAfterCheck}>Отметить как выполнено и дальше</button>
        </div>
      </div>

      {index === items.length - 1 && (
        <div style={{marginTop:20}}>
          <h4>Форма отчета</h4>
          <div>
            <input placeholder="Рейс (FlightNumber)" value={meta.flight_number} onChange={e=>setMeta({...meta, flight_number:e.target.value})} />
          </div>
          <div>
            <input placeholder="Район/Зона (Place)" value={meta.place} onChange={e=>setMeta({...meta, place:e.target.value})} />
          </div>
          <div>
            <input placeholder="Время события (TimeOfInc)" value={meta.time_of_incident} onChange={e=>setMeta({...meta, time_of_incident:e.target.value})} />
          </div>
          <div>
            <input placeholder="Дата (Date)" value={meta.date_of_incident} onChange={e=>setMeta({...meta, date_of_incident:e.target.value})} />
          </div>
          <div style={{marginTop:10}}>
            <textarea placeholder="Комментарий" value={comment} onChange={e=>setComment(e.target.value)} />
          </div>
          <div style={{marginTop:10}}>
            <button onClick={handleSubmit}>Отправить отчет</button>
          </div>
        </div>
      )}
      <div style={{marginTop:20}}>
        <div>Отмечено: {checkedIds.length} / {items.length}</div>
      </div>
    </div>
  );
}
